INSERT INTO `cmf_plugin_article_item` VALUES ('162', '5', null, '喜报 ——我公司在国家电网2014年采集系统建设专项批次招标中标', '1', '', '<p><span style=\"font-size: large;\"><img style=\"display: block; margin-left: auto; vertical-align: top; margin-right: auto;\" height=\"180\" src=\"/UploadFiles/Images//闻成龙/201311/3139674_084941003_2_副本.jpg.axd\" width=\"180\" /></span></p>\r\n<p><span style=\"font-size: small;\">&nbsp;&nbsp; </span>我公司在国家电网2014年采集系统建设专项批次招标中喜中电能表2383.76万元，电能表中标排名第11！</p>', '0', '', '', '', '闻成龙', '', '', '', '', '/UploadFiles/Images//闻成龙/201311/3139674_084941003_2_副本.jpg.axd', '', '224', '0', '', '1', '0', '0', '1', '0', '0', '2014-01-17 00:00:00', '2016-07-15 11:15:32', '2016-07-15 11:15:32', '2014-04-21 16:36:23', '0', '1', '0', '0');
INSERT INTO `cmf_plugin_article_item` VALUES ('163', '5', null, '喜报 ——我公司在国家电网2014年第一次招标中,中标近亿元', '1', '', '<div><span style=\"font-size: large;\"></span>&nbsp;<span style=\"font-size: large;\"><span style=\"font-size: large;\"><img style=\"display: block; margin-left: auto; vertical-align: middle; margin-right: auto;\" src=\"/UploadFiles/Images/闻成龙/201311/3139674_084941003_2_副本.jpg.axd?width=450\" width=\"225\" height=\"194\" /></span></span></div>\r\n<div><span style=\"font-size: large;\">&nbsp;&nbsp; <span style=\"font-size: small;\">中标表数为单相表383355只，三相表46300只，总金额9130.498955万元，电能表中标金额排名第21位。</span></span></div>\r\n<div><span style=\"font-size: small;\">&nbsp;&nbsp;&nbsp; 国家电网的成功入围及中标，是武汉盛帆电子各部门员工同心协力、共同努力的成果，希望在后续的过程中，各部门也能够齐心协力，为保质保量地完成供货任务而奋斗！</span></div>', '0', '', '', '', '闻成龙', '', '', '', '', '/UploadFiles/Images//闻成龙/201311/3139674_084941003_2_副本.jpg.axd', '', '450', '0', '', '1', '0', '0', '1', '0', '0', '2014-04-01 00:00:00', '2016-07-15 11:15:32', '2016-07-15 11:15:32', '2014-07-17 14:26:19', '0', '1', '0', '0');
