INSERT INTO `cmf_plugin_job_item` VALUES ('1', '3', '区域销售经理', '<p style=\"text-align: left;\">十年以上销售经验，五年以上电力行业、水表行业或其他垄断行业大区域销售、管理工作经验；<br />熟悉垄断行业销售模式，具有丰富的营销策划和公关销售能力；<br />对市场开拓有较强的进取意识和开创能力，主动创新意识强；</p>\r\n<p style=\"text-align: left;\">有一定的领导能力，能够建立并培养一支优秀的销售团队；</p>\r\n<p style=\"text-align: left;\">能适应经常出差或长驻外地，抗压能力强。</p>', '', '若干', '2013-10-01', '武汉', '大专及以上', '面议', '不限', '1125', '2013-09-01 00:00:00', '1', '0', '社会招聘', '', '', '', '');
INSERT INTO `cmf_plugin_job_item` VALUES ('4', '3', '技术支持（售后）工程师', '<p style=\"text-align: left; widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0px; font: 12px/1.5 宋体, \'Courier New\'; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\">电子信息工程、测控技术、通信工程、自动化等相关专业毕业；&nbsp;<br />具备良好的数字、模拟电路基础；&nbsp;<br />有较强的学习、自我完善和沟通能力，能承受较大的工作压力；</p>\r\n<p style=\"text-align: left; widows: 2; text-transform: none; background-color: #ffffff; text-indent: 0px; margin: 0px; font: 12px/1.5 宋体, \'Courier New\'; white-space: normal; orphans: 2; letter-spacing: normal; color: #000000; word-spacing: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px;\">能适应长期在全国范围出差，身体素质好；<br />2年以上电子产品行业测试、研发或技术支持经验，有在电能表、智能终端行业工作经验者优先</p>', '', '若干', '2013-10-1', '全国各地', '大专以上', '面议', '不限', '2032', '2013-09-03 00:00:00', '1', '0', '社会招聘', '', '', '', '');
