INSERT INTO `cmf_plugin_product_item` VALUES ('57', '3', null, '单相费控智能电能表', '1', '', '', '未选择', '未选择', '<p>单相费控智能电能表采用专用电能计量芯片、微处理器芯片及SMT生产工艺制造，集高精度的电能测量与科学的用电管理为一体的全新数字式电能表。用于测量额定频率为50Hz(或60Hz)的有功电能及用电参数，具有多费率、费控、事件记录等功能。其技术指标符合GB/T 17215.321-2008，DL/T 645-2007以及Q/GDW 355-2009，Q/GDW 354-2009，Q/GDW 364-2009，Q/GDW 365-2009等国家、行业和国网单相费控智能电能表技术相关技术标准。具有低功耗、防窃电、抗谐波等特点。<img src=\"/UploadFiles/Images//luckyxp/201306/10预付费载波正.jpg.axd\" /></p>', '0', '', '', '', '', '', '', '', '', '/UploadFiles/Images//luckyxp/201306/10预付费载波正.jpg.axd', '', '6532', '1', '0', '0', '1', '0', '0', '2013-06-19 00:00:00', '2016-07-15 11:15:48', '2016-07-15 11:15:48', '2013-06-19 21:53:39', '0', '1', '0', '0', '', '<div align=\"center\">\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\">\r\n<tbody>\r\n<tr>\r\n<td width=\"266\">\r\n<p>★低功耗、抗直流分量、长寿命、高精度</p>\r\n</td>\r\n<td width=\"291\">\r\n<p>★费控功能（本地费控/远程费控）</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"266\">\r\n<p>★抗高压攻击和静电攻击</p>\r\n</td>\r\n<td width=\"291\">\r\n<p>★通讯控制断、送电功能（继电器内置/外置）</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"266\">\r\n<p>★高温高湿环境保证计量精度</p>\r\n</td>\r\n<td width=\"291\">\r\n<p>★事件记录</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"266\">\r\n<p>★正向、反向有功电能计量</p>\r\n</td>\r\n<td width=\"291\">\r\n<p>★停电显示</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"266\">\r\n<p>★时钟及多费率</p>\r\n</td>\r\n<td width=\"291\">\r\n<p>★开盖检测功能</p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td width=\"266\">\r\n<p>★485、红外通讯</p>\r\n</td>\r\n<td width=\"291\">\r\n<p>★载波通讯（本地载波/远程载波）</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</div>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; （★基本功能特点&nbsp; ☆扩展功能特点）</p>