INSERT INTO `cmf_plugin_single_category` VALUES ('28', '{759E3B43-94F2-4A70-A798-3E17939FB9D6}', '', '2', 'Contact us', '', '', '18', '1', '<div class=\"SL_contact\">\r\n<script src=\"http://api.map.baidu.com/api?key=&amp;v=1.1&amp;services=true\" type=\"text/javascript\"></script>\r\n<div style=\"width: 892px; height: 480px; border: #ccc solid 4px;\" id=\"dituContent\"></div>\r\n<script type=\"text/javascript\">// <![CDATA[\r\n//创建和初始化地图函数：\r\n    function initMap(){\r\n        createMap();//创建地图\r\n        setMapEvent();//设置地图事件\r\n        addMapControl();//向地图添加控件\r\n        addMarker();//向地图中添加marker\r\n    }\r\n    //创建地图函数：\r\n    function createMap(){\r\n        var map = new BMap.Map(\"dituContent\");//在百度地图容器中创建一个地图\r\n        var point = new BMap.Point(114.372067,30.407622);//定义一个中心点坐标\r\n        map.centerAndZoom(point,16);//设定地图的中心点和坐标并将地图显示在地图容器中\r\n        window.map = map;//将map变量存储在全局\r\n    }\r\n    //地图事件设置函数：\r\n    function setMapEvent(){\r\n        map.enableDragging();//启用地图拖拽事件，默认启用(可不写)\r\n        map.disableScrollWheelZoom();//禁用地图滚轮放大缩小，默认禁用(可不写)\r\n        map.enableDoubleClickZoom();//启用鼠标双击放大，默认启用(可不写)\r\n        map.enableKeyboard();//启用键盘上下左右键移动地图\r\n    }\r\n    //地图控件添加函数：\r\n    function addMapControl(){\r\n        //向地图中添加缩放控件\r\n	var ctrl_nav = new BMap.NavigationControl({anchor:BMAP_ANCHOR_TOP_LEFT,type:BMAP_NAVIGATION_CONTROL_LARGE});\r\n	map.addControl(ctrl_nav);\r\n        //向地图中添加缩略图控件\r\n	var ctrl_ove = new BMap.OverviewMapControl({anchor:BMAP_ANCHOR_BOTTOM_RIGHT,isOpen:1});\r\n	map.addControl(ctrl_ove);\r\n        //向地图中添加比例尺控件\r\n	var ctrl_sca = new BMap.ScaleControl({anchor:BMAP_ANCHOR_BOTTOM_LEFT});\r\n	map.addControl(ctrl_sca);\r\n    }\r\n    //标注点数组\r\n    var markerArr = [{title:\"Owned by WUHAN SAN FRAN ELECTRONICS CO.,LTD.\",content:\"No.9 Yangguang avenue, Jiangxia economic development zone,Wuhan,China.\",point:\"114.369103|30.406065\",isOpen:1,icon:{w:23,h:25,l:46,t:21,x:9,lb:12}}\r\n		 ];\r\n    //创建marker\r\n    function addMarker(){\r\n        for(var i=0;i<markerArr.length;i++){\r\n            var json = markerArr[i];\r\n            var p0 = json.point.split(\"|\")[0];\r\n            var p1 = json.point.split(\"|\")[1];\r\n            var point = new BMap.Point(p0,p1);\r\n			var iconImg = createIcon(json.icon);\r\n            var marker = new BMap.Marker(point,{icon:iconImg});\r\n			var iw = createInfoWindow(i);\r\n			var label = new BMap.Label(json.title,{\"offset\":new BMap.Size(json.icon.lb-json.icon.x+10,-20)});\r\n			marker.setLabel(label);\r\n            map.addOverlay(marker);\r\n            label.setStyle({\r\n                        borderColor:\"#808080\",\r\n                        color:\"#333\",\r\n                        cursor:\"pointer\"\r\n            });\r\n			(function(){\r\n				var index = i;\r\n				var _iw = createInfoWindow(i);\r\n				var _marker = marker;\r\n				_marker.addEventListener(\"click\",function(){\r\n				    this.openInfoWindow(_iw);\r\n			    });\r\n			    _iw.addEventListener(\"open\",function(){\r\n				    _marker.getLabel().hide();\r\n			    })\r\n			    _iw.addEventListener(\"close\",function(){\r\n				    _marker.getLabel().show();\r\n			    })\r\n				label.addEventListener(\"click\",function(){\r\n				    _marker.openInfoWindow(_iw);\r\n			    })\r\n				if(!!json.isOpen){\r\n					label.hide();\r\n					_marker.openInfoWindow(_iw);\r\n				}\r\n			})()\r\n        }\r\n    }\r\n    //创建InfoWindow\r\n    function createInfoWindow(i){\r\n        var json = markerArr[i];\r\n        var iw = new BMap.InfoWindow(\"<b class=\'iw_poi_title\' title=\'\" + json.title + \"\'>\" + json.title + \"</b><div class=\'iw_poi_content\'>\"+json.content+\"</div>\");\r\n        return iw;\r\n    }\r\n    //创建一个Icon\r\n    function createIcon(json){\r\n        var icon = new BMap.Icon(\"http://app.baidu.com/map/images/us_mk_icon.png\", new BMap.Size(json.w,json.h),{imageOffset: new BMap.Size(-json.l,-json.t),infoWindowOffset:new BMap.Size(json.lb+5,1),offset:new BMap.Size(json.x,json.h)})\r\n        return icon;\r\n    }\r\n    initMap();//创建和初始化地图\r\n// ]]></script>\r\n<div class=\"text clearfix\">\r\n<div class=\"left\">\r\n<p><span>Address:</span>No.9 Yangguang avenue, Jiangxia economic development zone,Wuhan,China</p>\r\n<p><span>Telephone number:</span>027-81802522</p>\r\n<p><span>Fax:</span>027-81800206、81800216、81800652</p>\r\n<p><span>Market:</span>027-81800252</p>\r\n<p><span>After service:</span>027-81800326、81802561 （24 hours hot line:13071235998）</p>\r\n</div>\r\n<div class=\"right\">\r\n<p>E-mail</p>\r\n<p><span>Company:</span>sftnow@163.com</p>\r\n<p><span>Market:</span>Market_sftnow@163.com</p>\r\n<p><span>After service:</span>kf@sftnow.com</p>\r\n</div>\r\n</div>\r\n</div>', '', '', '', '', '', '0', '', '2013-05-30 11:34:32');
